mapper.select:
SELECT 
FROM tag t
WHERE t.id = #{id}

mapper.insert:
INSERT INTO tag (
    name, mac_address, model, firmware_version, 
    map_id, rssi, position_x, position_y, position_z, 
    battery_level, status, last_seen, create_time, remark
)
VALUES (
    #{name}, #{macAddress}, #{model}, #{firmwareVersion}, 
    #{mapId}, #{rssi}, #{positionX}, #{positionY}, #{positionZ}, 
    #{batteryLevel}, #{status}, #{lastSeen}, #{createTime}, #{remark}
)

mapper.update:
UPDATE tag
name = #{name}, mac_address = #{macAddress}, model = #{model}, firmware_version = #{firmwareVersion}, map_id = #{mapId}, rssi = #{rssi}, position_x = #{positionX}, position_y = #{positionY}, position_z = #{positionZ}, battery_level = #{batteryLevel}, status = #{status}, last_seen = #{lastSeen}, remark = #{remark}
WHERE id = #{id}

UPDATE tag
SET status = 0
WHERE status = 1 AND last_seen < #{thresholdTime}
