mapper.insert:
INSERT INTO station (
    code, name, mac_address, ip_address, model, firmware_version,
    map_id, position_x, position_y, position_z, orientation,
    status, scan_enabled, last_communication, create_time, remark
)
VALUES (
    #{code}, #{name}, #{macAddress}, #{ipAddress}, #{model}, #{firmwareVersion},
    #{mapId}, #{positionX}, #{positionY}, #{positionZ}, #{orientation},
    #{status}, #{scanEnabled}, #{lastCommunication}, #{createTime}, #{remark}
)

mapper.update:
UPDATE station
code = #{code}, name = #{name}, mac_address = #{macAddress}, ip_address = #{ipAddress}, model = #{model}, firmware_version = #{firmwareVersion}, map_id = #{mapId}, position_x = #{positionX}, position_y = #{positionY}, position_z = #{positionZ}, orientation = #{orientation}, status = #{status}, scan_enabled = #{scanEnabled}, last_communication = #{lastCommunication}, remark = #{remark}
WHERE id = #{id}

UPDATE station
SET status = #{status}, 
    last_communication = #{lastCommunication}
WHERE id = #{id}

UPDATE station
SET status = #{status}, 
    scan_enabled = #{scanEnabled},
    last_communication = #{lastCommunication}
WHERE id = #{id}
