mapper.select:
SELECT 
FROM tag t
WHERE t.id = #{id}

SELECT 
FROM tag t
WHERE t.mac_address = #{macAddress}

SELECT 
FROM tag t
WHERE t.code = #{code}

mapper.insert:
INSERT INTO tag (
    code, name, group_name, mac_address, model, firmware_version, 
    map_id, map_name, rssi, position_x, position_y, position_z, 
    battery_level, status, last_seen, create_time, remark
)
VALUES (
    #{code}, #{name}, #{groupName}, #{macAddress}, #{model}, #{firmwareVersion}, 
    #{mapId}, #{mapName}, #{rssi}, #{positionX}, #{positionY}, #{positionZ}, 
    #{batteryLevel}, #{status}, #{lastSeen}, #{createTime}, #{remark}
)

mapper.update:
UPDATE tag
code = #{code}, name = #{name}, group_name = #{groupName}, mac_address = #{macAddress}, model = #{model}, firmware_version = #{firmwareVersion}, map_id = #{mapId}, map_name = #{mapName}, rssi = #{rssi}, position_x = #{positionX}, position_y = #{positionY}, position_z = #{positionZ}, battery_level = #{batteryLevel}, status = #{status}, last_seen = #{lastSeen}, remark = #{remark}
WHERE id = #{id}
